(function() {
  var collection_beforeSend, collection_onload, collection_success, collection_tagchange, collection_update_success;

  window.RP = window.RP || {};

  RP.collection = RP.collection || {};

  jQuery(function() {
    var elmt, navlinks;
    $("#tagstxt").tokenInput("http://www.recipepower.com/tags/match.json", {
      crossDomain: false,
      hintText: "",
      noResultsText: "No matching tag found; hit Enter to search with text",
      prePopulate: $("#tagstxt").data("pre"),
      theme: "facebook",
      onAdd: collection_tagchange,
      onDelete: collection_tagchange,
      allowFreeTagging: true,
      placeholder: "Seek and ye shall find...",
      zindex: 1500
    });
    $(window).resize(function() {
      var elmt, navlinks;
      if ((elmt = $("div.browser_house")[0]) && (navlinks = $('div#footer_nav_links')[0])) {
        return elmt.style.bottom = (navlinks.offsetHeight + 5).toString() + "px";
      }
    });
    if ((elmt = $("div.browser_house")[0]) && (navlinks = $('div#footer_nav_links')[0])) {
      elmt.style.bottom = (navlinks.offsetHeight + 5).toString() + "px";
    }
    $('a.update-button').click(function() {
      var url;
      if (url = $(this).data("url")) {
        return RP.get_content(url, 'a.update-button');
      }
    });
    collection_onload();
    return RP.fire_triggers();
  });

  collection_onload = function() {
    $("#tagstxt").first().focus();
    $('.pageclickr').bind("ajax:beforeSend", collection_beforeSend);
    $('.pageclickr').bind("ajax:success", collection_success);
    checkForLoading(".stuffypic");
    RP.rcp_list.onload();
    return window.setTimeout(function() {
      return RP.collection.justify();
    }, 800);
  };

  collection_tagchange = function() {
    var formitem;
    formitem = $('form.query_form');
    if ($(formitem).data("format") === "html") {
      return formitem.submit();
    } else {
      return RP.collection.update($(formitem).serialize(), $(formitem).attr("action"));
    }
  };

  collection_success = function(evt, data, status, xhr) {
    return collection_update_success(data);
  };

  collection_beforeSend = function(evt, xhr, settings) {
    RP.dialog.cancel();
    RP.notifications.wait();
    return true;
  };

  collection_update_success = function(resp) {
    $('#masonry-container').masonry('destroy');
    RP.process_response(resp);
    window.scrollTo(0, 0);
    RP.notifications.done();
    return collection_onload();
  };

  RP.collection.update = function(params, url) {
    collection_beforeSend();
    return jQuery.ajax({
      type: "POST",
      url: url || "/collection/query",
      data: params,
      dataType: "json",
      beforeSend: function(xhr) {
        return xhr.setRequestHeader('X-CSRF-Token', $('meta[name="csrf-token"]').attr('content'));
      },
      error: function(jqXHR, textStatus, errorThrown) {
        var responseData;
        RP.notifications.done();
        responseData = RP.post_error(jqXHR);
        return RP.process_response(responseData);
      },
      success: function(resp, succ, xhr) {
        return collection_update_success(resp);
      }
    });
  };

  RP.collection.rejustify = function() {
    return $('#masonry-container').masonry();
  };

  RP.collection.justify = function() {
    return $('#masonry-container').masonry({
      columnWidth: 220,
      gutter: 20,
      itemSelector: '.masonry-item'
    });
  };

}).call(this);
